import React, { useState, useContext, useEffect } from 'react'
import { Avatar, Text } from 'react-native-elements';
import { View, ScrollView, ActivityIndicator } from 'react-native';
import { AuthContext } from '../Componenets/AuthContext';
import helpers from '../helpers/helperFunctions';
import styleSheet from '../Pages/PageStyle';
import uuid from 'react-native-uuid';

export default function FCAvatarScrollView() {
  const [avatars, setAvatars] = useState([]);
  const [isDataFecthed, setDataFetched] = useState(false);
  const { user, authenticated } = useContext(AuthContext);

  useEffect(() => {
    if (authenticated) {
      getAvatars();
    }
  }, [authenticated]);

  const getAvatars = async () => {
    await fetch(helpers.getApi() + '/User/getProximity?email=' + user.email,
      {
        method: 'GET',
        headers: new Headers({
          'Content-Type': 'application/json; charset=UTF-8',
          'Accept': 'application/json; charset=UTF-8',
        })
      })
      .then(res => {
        return res.json();
      })
      .then(
        (result) => {

          setAvatars(result);
          setDataFetched(true);

        },
        (error) => {
          console.log("err post=", error);
        });
  }

  if ( isDataFecthed ) {
    return (
      <ScrollView
        horizontal={true}
        pagingEnabled={true}
        style={styleSheet.scrollView}>
        {avatars.map(item => (
          <View key={uuid.v4()} style={{ alignItems: 'center', padding: 10 }}>
            <Avatar
              rounded={true}
              size="large"
              containerStyle={{
                borderWidth: 4,
                shadowColor: "#000",
                shadowOffset: {
                  width: 0,
                  height: 2,
                },
                shadowOpacity: 0.27,
                shadowRadius: 4.65,
                elevation: 6,
                borderColor: helpers.ReturnRandomData(helpers.ReturnColorPallete())
              }}
              source={{
                uri:
                  item.picture,
              }}
              icon={{ name: 'user', type: 'font-awesome' }}
            />
            <Text style={[styleSheet.textInput]}>{item.Name}</Text>
          </View>

        ))}
      </ScrollView>
    )
  }
  else {
    return (
      <ActivityIndicator size='small' />
    )
  }

}
