import React, { useState, useContext } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity
} from 'react-native';
import {  useNavigation } from '@react-navigation/native';
import { AuthContext } from '../Componenets/AuthContext';
import helpers from '../helpers/messages.json';

const FCProfile = () => {
  const navigation = useNavigation();
  const { user, authenticated, logout } = useContext(AuthContext);

  
  if(!authenticated)
  {
    return(
      <View></View>
    )
  }
  return (
    <View style={styles.container}>
      <View style={styles.header}></View>
      <Image style={styles.avatar} source={{ uri: user.picture }} />
      <View style={styles.body}>
        <View style={styles.bodyContent}>
          <Text style={styles.name}>{user.name}</Text>
          <TouchableOpacity 
            style={styles.buttonContainer}
            onPress={()=>{
              logout();
              navigation.push('waitPage')
            }}
          >
            <Text style={styles.text}>{helpers.logout}</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.buttonContainer}>
            <Text style={styles.text}></Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  )

}
const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor: '#fff'
  },
  header:{
    backgroundColor: "#F0E6E8",
    height:200,
  },
  text:{
    color:'white'
  },
  avatar: {
    width: 130,
    height: 130,
    borderRadius: 63,
    borderWidth: 4,
    borderColor: "white",
    marginBottom:10,
    alignSelf:'center',
    position: 'absolute',
    marginTop:130
  },
  name:{
    fontSize:22,
    fontWeight:'600',
  },
  body:{
    marginTop:40,
  },
  bodyContent: {
    // flex: 1,
    alignItems: 'center',
    padding:30,
  },
  name:{
    fontSize:28,
    color: "#691A1A",
    fontWeight: "600"
  },
  info:{
    fontSize:16,
    color: "#00BFFF",
    marginTop:10
  },
  buttonContainer: {
    marginTop:10,
    height:45,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom:20,
    width:250,
    borderRadius:30,
    backgroundColor: "#691A1A",
  },
});
export default FCProfile;
