import React, { useState, useEffect, useContext } from 'react';
import { View, Image, Text, ScrollView } from 'react-native';
import { Avatar } from 'react-native-elements';
import styleSheet from '../Pages/PageStyle'
import helpers from '../helpers/helperFunctions';
import { AuthContext } from '../Componenets/AuthContext';
import FCLikes from './FCLikes';
import FCShare from './FCShare';
import FCWineryAvatar from './FCWineryAvatar';

function FCEventsScrollView() {
  const [events, setEvents] = useState([]);
  const { authenticated, user } = useContext(AuthContext);

  useEffect(() => {
    getEvents();
    return () => {
      setEvents([]);
    }
  }, []);

  function getEvents() {
    fetch(helpers.getApi() + '/Event/GetAllEvents',
      {
        method: 'GET',
        headers: new Headers({
          'Content-Type': 'application/json; charset=UTF-8',
          'Accept': 'application/json; charset=UTF-8',
        })
      })
      .then(res => {
        return res.json();
      })
      .then(
        (result) => {
          // console.log(result);
          setEvents(result);
        },
        (error) => {
          console.log("err post=", error);
        });
  }





  return (
    <ScrollView
      horizontal={true}
      pagingEnabled={true}
      style={[styleSheet.scrollView]}>
      <View style={{ flexDirection: 'row' }}>
        {events.map(item => (
          <View key={item.eventId} >
            <View style={styleSheet.rowEvents} >
              <FCWineryAvatar wineryName={item.wineryName} wineryImage={item.wineryImage} />
              <View style={{ flexDirection: 'row-reverse', justifyContent: 'space-evenly', marginTop: 10 }}>
                <Text style={{ fontWeight: 'bold' }}>{item.eventName}</Text>
                <Text style={{ fontWeight: 'bold' }}>{helpers.ReturnDate(item.Date)}</Text>
              </View>
              <Image
                source={{ uri: item.eventImgPath }}
                style={styleSheet.event} />

            </View>
            <View>
              <FCLikes likes={item.likes} entityId={item.eventId} entityType={1} />
            </View>
          </View>

        ))}
      </View>
    </ScrollView>

  )
}
export default FCEventsScrollView;
