import React, { useState, useEffect, useContext } from 'react';
import { StyleSheet, View, TouchableOpacity, Dimensions } from 'react-native';
import { Text } from 'react-native-elements'
import { Camera } from 'expo-camera';
import { AuthContext } from '../Componenets/AuthContext';
import Circle from './FCTest';
import helpers from '../helpers/helperFunctions';

export default function App() {
  const [hasPermission, setHasPermission] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);
  const [photoUri, setsphotoUri] = useState({ uri: 'https://cdn4.iconfinder.com/data/icons/ionicons/512/icon-image-512.png' })
  const { authenticated } = useContext(AuthContext);
  const bWidth = Dimensions.get('window').width;
  const bHeight = Dimensions.get('window').height;

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  const btnSnap = async () => {
    let photo = await Camera.takePictureAsync({
      quality: 0.4,
      base64: true,
      exif: false
    }, () => { console.log(photo) });
    setsphotoUri({
      photoUri: photo.uri,
    });
    RecognizePicture();
  };

  const RecognizePicture = async (picUrl  , prefix) => {
    let Label =
    {
      picUrl:picUrl,
      prefix: prefix
    };

    await fetch(helpers.getApi() + '/ImageRecognize',
      {
        method: 'POST',
        body: JSON.stringify(Label),
        headers: new Headers({
          'Content-Type': 'application/json; charset=UTF-8',
          'Accept': 'application/json; charset=UTF-8',
        })
      })
      .then(res => {
        console.log(res);
        return JSON.stringify(res);
      }, (error) => {
        alert(error);
      })
  }

  if (hasPermission === null) {
    return <View />;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }
  if (!authenticated) {
    return <Circle/> /// Just kidding . should have a pop up message
  }
  return (
    <View style={[styles.container, {
      flexDirection: 'column'
    }]}>
      <Camera style={styles.camera} type={type}>
        <TouchableOpacity onPress={btnSnap}>
          <View style={{
            flex: 1,
            flexDirection: 'row',
            justifyContent: 'space-between',
          }}>
            <View style={{
              width: bWidth * 0.20,
              height: bWidth * 0.20,
              backgroundColor: 'red',
              marginTop: bHeight * 0.75,
              marginLeft: bWidth * 0.4,
              borderRadius: 100,
              borderColor: 'black',
              borderStyle: 'solid',
              borderWidth: '6'
            }}
            />
          </View>
        </TouchableOpacity>
      </Camera>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  camera: {
    flex: 1,
  },
  buttonContainer: {
    flex: 1,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    margin: 20,
  },
  button: {
    flex: 0.1,
    alignSelf: 'flex-end',
    alignItems: 'center',
  },
  text: {
    fontSize: 18,
    color: 'white',
  }
});
